import { from, lastValueFrom, switchMap } from "rxjs";
export function initializeKeycloak(keycloak, configService) {
    return async () => await lastValueFrom(configService.getConfig()
        .pipe(switchMap((config) => {
        return from(keycloak.init({
            config: {
                url: config['KEYCLOAK_URL'] + '/auth',
                realm: config['KEYCLOAK_REALM'],
                clientId: config['KEYCLOAK_CLIENT_ID'],
            }
        }));
    })));
}
