import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Student, StudentAttributes } from '../models/student';
import * as i0 from "@angular/core";
import * as i1 from "../services/user.service";
import * as i2 from "@angular/forms";
export class DashboardComponent {
    constructor(userService) {
        this.userService = userService;
        this.studentCreationForm = new FormGroup({
            firstName: new FormControl(null, Validators.required),
            lastName: new FormControl(null, Validators.required),
            email: new FormControl(null, [Validators.required, Validators.email]),
            regNumber: new FormControl(null, Validators.required),
            grade: new FormControl(null)
        });
    }
    ngOnInit() {
    }
    onSubmit() {
        if (this.studentCreationForm.valid) {
            var studentAttributes = new StudentAttributes(this.studentCreationForm.value.regNumber, this.studentCreationForm.value.grade);
            var student = new Student(this.studentCreationForm.value.firstName, this.studentCreationForm.value.lastName, this.studentCreationForm.value.email, studentAttributes);
            this.userService.createStudent(student).subscribe({
                next: async (response) => {
                    console.log(response);
                },
                error: (error) => {
                    console.log(error);
                }
            });
        }
        else {
            console.log(this.studentCreationForm.errors);
        }
    }
}
